import React, { useState } from 'react';
import lamp from './img/lamp.svg';
import './App.css';




function App() { 
  const [on, toggleSwitch] = useState(!true)

   return (
    <div className="App">
      <header className="App-header">

    <button onClick={()=>toggleSwitch(!on)}>{on ? 'Turn it off' : 'Turn it on'}</button>
    {on ? <p className="on">The lamp is on.</p> : <p className="off">The lamp is off.</p>}
    <img src="lamp"></img>
      </header>
    </div>
  )
   }

export default App;
