    on = !on;

  }

   return (
    <div className="App">
      <header className="App-header">
    <p>Hello</p>
    <button onClick={toggleSwitch}>Lamp</button>
    render()
   {
     on===!true ?  <p>The lamp is on.</p> :
    <p>The lamp is off.</p>}
      </header>
