   filtersHandlers()

   const [filter, setFilter] = useState('all');
  return(
      <div>  
         <p>{filter}</p>
         <div className="filters">
            <div className="filter">
               <p>Choose what you love</p>
            <select name="category" id="category-select" className="categories" onChange={this.setFilter(event.target.value)}>
               <option value="all">All that we love</option>
               <option value="adventure">Adventures</option>
               <option value="detective">Detectives</option>
               <option value="roman">Romans</option>
            </select>
            </div>
         </div>
         <div className="books-list">
            {renderedBooks}
