import React, { useState } from 'react';
import lamp from './img/lamp.png';
import './App.css';




function App() { 
  const [on, toggleSwitch] = useState(!true)

   return (
    <div className="App">
      <header className="App-header">
    {on ? <p className="on">The lamp is on.</p> : <p className="off">The lamp is off.</p>}
    <img onClick={()=>toggleSwitch(!on)} src={lamp} alt="Here might be a lamp" />
    {on ? <p className="on">Turn it off</p> : <p className="off">Turn it on</p>}
      </header>
    </div>
  )
   }

export default App;
