import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';




function App() { 
  const [on, toggleSwitch] = useState(!true)

   return (
    <div className="App">
      <header className="App-header">

    <button onClick={()=>toggleSwitch(!on)}>{on ? <p>Turn it off.</p> : <p>Turn it on.</p>}</button>
    {on ? <p>The lamp is on.</p> : <p>The lamp is off.</p>}
      </header>
    </div>
  )
   }

export default App;

