import React from 'react';
import logo from './logo.svg';
import './App.css';


function App() { 

  const rnd = Math.ceil(Math.random()*10)
  let predict = prompt("What is your predict?", 1)
  function answ(rnd, predict){
    const win = {
      color: "white"
    }
    const loose = {
      color: "red"
    }
    let s = rnd == predict ? {win} : {loose}
  }
  return (

    <div className="App">
      <header className="App-header">
      <p>Try to predict number from 1 to 10!</p>

      <p style={{s}}>This time number is {rnd}</p>
      </header>

      <img src={logo} className="App-logo" alt="logo" />
    </div>

  );
}

export default App;
