import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';




function App() { 
  let on = !true;
  const toggleSwitch = () => {
    on = !on;
    if(on==true) log = <p>The lamp is on.</p>;
    else log = <p>The lamp is off.</p>
  }
  let log = <p>The lamp is on.</p>;
  class Lamp extends React.Component{
    constructor(color, on = false){
      this.color = color;
      this.on = on;
   }
   toggleSwitch(on){
     this.on = !this.on;
     console.log(this.on)
     }
   state(on){
    return this.on ===true ? `<p>The lamp is on.</p>` : `<p>The lamp is off.</p>`
   }
   }
   return (
    <div className="App">
      <header className="App-header">
    <p>Hello</p>
    <button onClick={toggleSwitch}>Lamp</button>
    {log}
      </header>
    </div>
  );
}

export default App;
