    log = on===!true ?  <p>The lamp is on.</p> : <p>The lamp is off.</p>



   return (
    <div className="App">
      <header className="App-header">
    <p>Hello</p>
    <button onClick={()=>toggleSwitch(on = !on )}>Lamp</button>
    {on ? <p>The lamp is on.</p> : <p>The lamp is off.</p>}
      </header>
    </div>
  )
   }

export default App;

