import logo from './logo.svg';
import './App.css';



let on = !true;
var log = <p>The lamp is on.</p>;
function App() { 

  const toggleSwitch = () => {
    on = on ==true ? false : true;
    console.log(on)
    log = on===!true ?  <p>The lamp is on.</p> : <p>The lamp is off.</p>
  }

   return (
    <div className="App">
      <header className="App-header">
    <p>Hello</p>
    <button onClick={toggleSwitch}>Lamp</button>
    {log}

      </header>
    </div>
  )
}

export default App;

