import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';




function App() { 
  //let on = !true;
  let log = <p>The lamp is on.</p>;
  const [on, toggleSwitch] = useState(!true)
  const cl = () => {
   toggleSwitch(
    on = on ==true ? false : true,
    console.log(on),
    log = on===true ?  <p>The lamp is on.</p> : <p>The lamp is off.</p>
   )
  }

   return (
    <div className="App">
      <header className="App-header">
    <p>Hello</p>
    <button onClick={cl}>Lamp</button>
    {log}
      </header>
    </div>
  )
}

export default App;

