import logo from './logo.svg';
import './App.css';




function App() { 
  const log = () => Lamp.on ===true ? `<p>The lamp is on.</p>` : `<p>The lamp is off.</p>`;
  class Lamp extends React.Component{
    constructor(color, on = false){
      this.color = color;
      this.on = on;
   }
   toggleSwitch(on){
     this.on = !this.on;
     console.log(this.on)
     }
   state(on){
    return this.on ===true ? `<p>The lamp is on.</p>` : `<p>The lamp is off.</p>`
   }
   }
   return (
    <div className="App">
      <header className="App-header">
    <p>Hello</p>
    <button onClick={log}>Lamp</button>
    {Lamp.state}
      </header>
    </div>
  );
}

export default App;
